mapper.insert:

			INSERT INTO OUATH2_TOKEN
                ( USERNAME
                , TOKEN
                , issuedDate
                ,issuedTime
                ,expiredDate
                ,expiredTime
                ,remoteIP
                ,STATUS
                ,USERTYPE
                )
            values (
                 #{userName}
                ,#{token}
                ,#{issuedDate}
                ,#{issuedTime}
                ,#{expiredDate}
                ,#{expiredTime}
                ,#{remoteIP}
                ,#{status}
                ,#{userType}
            )

mapper.update:

UPDATE OUATH2_TOKEN
       SET token 		= #{token}
        , issuedDate 	= #{issuedDate}
        , issuedTime  	= #{issuedTime}
        , expiredDate  = #{expiredDate}
        , expiredTime  = #{expiredTime}
        , remoteIP  	= COALESCE( #{remoteIP}, '')
        , STATUS  		= #{status}
        , USERTYPE   	= #{userType}
WHERE USERNAME = #{userName}
